import React, { Component } from "react";

export default class Explainer extends Component {
  render() {
    return (
      <div className="flex flex-1 background flex justify-center items-center">

        <div className="flex flex-col lg:flex-row lg:flex-3 justify-center items-center p-48">
        <img
          className="lg:p-48"
          src="/static/ebook.png"
          alt="GoodUp Ebook"
          width="50%"
        />
        <div className="flex flex-2 items-center mt-24 mb-48">
            <ul className="shadow-7 list-inside list-disc pl-24 pr-48 pt-24 pb-24 bg-white rounded-lg">
                <li className="font-edmondsans-medium text-green text-16 leading-loose">Leverage your e-book to Get 11+ Leads / Day!</li>
                <li className="font-edmondsans-medium text-green text-16 leading-loose">Easily create your e-book image</li>
                <li className="font-edmondsans-medium text-green text-16 leading-loose">Quickly find unlimited topics for your e-books</li>
                <li className="font-edmondsans-medium text-green text-16 leading-loose">Become THE leader in your industry</li>
                <li className="font-edmondsans-medium text-green text-16 leading-loose">ZERO tech skills required - takes just minutes</li>
            </ul>
        </div>
        </div>

        <style jsx>{`
          .background {
            background-image: linear-gradient(to right, #291e50, #4a3a83);
          }
        `}</style>
      </div>
    );
  }
}
