// googleAnalytics.js

import ReactGA from "react-ga"

export const initGA = () => {
  ReactGA.initialize("UA-xxxxxxxxx-1")
}

export const logPageView = () => {
  ReactGA.set({ page: window.location.pathname })
  ReactGA.pageview(window.location.pathname)
}
